# Funktion um eine Frage zu beantworten
def answer_question(model_name, question):
  tokenizer = AutoTokenizer.from_pretrained("data/dwolpers/" + model_name)
  model = T5ForConditionalGeneration.from_pretrained("data/dwolpers/" + model_name)
  input_ids = tokenizer("Beantworte die Frage: " + question, return_tensors="pt").input_ids
  gen_tokens = model.generate(input_ids, max_length=300)
  return tokenizer.batch_decode(gen_tokens, skip_special_tokens=True)[0]

# Funktion um eine Frage zu beantworten
def answer_question_open_context(model_name, question, context):
  tokenizer = AutoTokenizer.from_pretrained("data/dwolpers/" + model_name)
  model = T5ForConditionalGeneration.from_pretrained("data/dwolpers/" + model_name)
  input_ids = tokenizer("Beantworte mit folgendem Kontext die Frage: " + question + ' ' + context, return_tensors="pt").input_ids
  gen_tokens = model.generate(input_ids, max_length=300)
  return tokenizer.batch_decode(gen_tokens, skip_special_tokens=True)[0]

